INSERT INTO `prosedur_kp_detail` (`no`, `no_prosedur`, `prosedur`, `id_judul`) VALUES
(1, '1', 'Peserta KP mencari tempat KP, dan memastikan Tempat KP bersedia menerima ybs', 1),
(2, '2', 'Peserta KP membuat surat Permohonan KP untuk tempat KP sbb :\r\n- Meminta surat ke TU KP\r\n- Surat ditandatangani pimpinan PRODI\r\n- Surat harus dilampiri oleh proposal KP (di download di portal KP dan di cap)\r\n- Surat diserahkan langsung ke tempat KP oleh ybs', 1),
(3, '3', 'Bila telah diterima KP-nya :\r\n- Menyerahkan form Kesediaan dari tempat KP, dan form profile pembimbing lapangan\r\n- Menyerahkan form pernyataan KP (di download), untuk penetapan dosen pembimbing\r\n- Semua dokumen diserahkan langsung ke Koordinator KP\r\n- Koordinator KP akan menetapkan pembimbing dan menyerahkan seluruh dokumen ke TU KP untuk diarsipkan', 1),
(4, '4', 'Bila peserta telah memiliki dosen pembimbing :\r\n- Bertemu dengan dosen pembimbing dengan membawa form rencana kegiatan yang telah diisi.\r\n- Diskusikan kegiatan yang akan dilakukan dengan dosen pembimbing.\r\n- Mulai Kerja Praktek di lapangan.', 1),
(5, '5', 'Pembimbingan :\r\n- Dilakukan di tempat KP dengan pembimbing lapangan dan dengan dosen pembimbing.\r\n- Bimbingan dengan dosen pembimbing adalah optional, sedangkan dengan pembimbing lapangan adalah wajib.\r\n- Pembimbingan dengan dosen pembimbing dapat melalui portal KP\r\n- Seluruh hasil kegiatan KP dan pembimbingan KP harus di entry ke portal KP (syarat seminar KP)', 1),
(6, '6', 'Penyelesaian KP\r\n- Telah dinyatakan selesai oleh tempat KP, ada surat pernyataan selesai\r\n- Meminta surat Penilaian KP dari TU KP, dan diserahkan ke tempat KP (kecuali tempat KP punya format sendiri)\r\n- Membuat laporan akhir KP (boleh dibimbing oleh dosen pembimbing)\r\n- Mengajukan seminar KP (disetujui dosen pembimbing)\r\n- Mengumpulkan laporan akhir KP (untuk di Prodi dan Tempat KP bila dibutuhkan)', 1),
(7, '1', 'Telah dinyatakan selesai oleh tempat Kerja Praktek (melampirkan bukti selesai dan Penilaian dari tempat Kerja Praktek.) Form penilaian dapat diminta ke TU TA.', 2),
(8, '2', 'Mengisi data seminar KP di portal KP (sesuai kalender KP).', 2);
